        private static List<NamedCurve> ecList = new List<NamedCurve>();

        private readonly String name;
        private readonly Asn1ObjectIdentifier oid;
        private readonly EECParameters ecParameters;
        private readonly int keyLen;

      
        public NamedCurve(String name, Asn1ObjectIdentifier oid, EECParameters ecParameters, int keyLen)
        {
            this.name = name;
            this.oid = oid;
            this.ecParameters = ecParameters;
            this.keyLen = keyLen;
        }

        private static void add(String name, String oidStr, String ecParamsStr, int keyLen)
        { 
            EECParameters ecParams = new EECParameters(StringUtil.ToByteArray(ecParamsStr));              
                   
            int [] oid = OIDUtil.parse(oidStr);
            Asn1ObjectIdentifier objectIdentifier = new Asn1ObjectIdentifier(oid);

            NamedCurve namedCurve = new NamedCurve(name, objectIdentifier, ecParams, keyLen);
            ecList.Add(namedCurve);
        }
 
