        public EECParameters getECParameters()
        {
            return ecParameters ;
        }

        public Asn1ObjectIdentifier getCurveOid()
        {
            return oid;
        }

        public string getName()
        {
            return name;
        }

        public int getKeyLen()
        {
            return keyLen;
        }

        public static NamedCurve getCurveParametersFromName(String name)
        {
            foreach (NamedCurve namedCurve in ecList)
            {
                if (namedCurve.name.Contains(name))
                {
                    return namedCurve;
                }
            }
            throw new ESYAException("Curve not recognized..");
        }

        public static NamedCurve getCurveByECParameters(EECParameters ecParameters)
        {
            foreach (NamedCurve namedCurve in ecList)
            {
                if (namedCurve.ecParameters.Equals(ecParameters))
                {
                    return namedCurve;
                }
            }
            throw new ESYAException("Curve not recognized..");
        }

        public static NamedCurve getCurveParametersFromOid(Asn1ObjectIdentifier curveOid)
        {
            foreach (NamedCurve namedCurve in ecList)
            {
                if (namedCurve.oid.Equals(curveOid))
                {
                    return namedCurve;
                }
            }
            throw new ESYAException("Curve not recognized..");
        }
