package tr.gov.tubitak.uekae.esya.api.xmlsignature.model.xades;

import tr.gov.tubitak.uekae.esya.api.xmlsignature.model.Any;
import tr.gov.tubitak.uekae.esya.api.xmlsignature.Context;
import tr.gov.tubitak.uekae.esya.api.xmlsignature.XMLSignatureException;
import static tr.gov.tubitak.uekae.esya.api.xmlsignature.core.Constants.*;
import org.w3c.dom.Element;

/**
 * This element contain any property that qualify some of the signed data
 * objects. The signature generated by the signer does not cover the content of
 * this element.
 *
 * @author ahmety
 * date: Oct 2, 2009
 */
public class UnsignedDataObjectProperty extends Any
{

    /**
     * Construct new BaseElement according to context
     * @param aContext where some signature spesific properties reside.
     */
    public UnsignedDataObjectProperty(Context aContext)
    {
        super(aContext);
    }

    /**
     * Construct UnsignedDataObjectProperty from existing
     * @param aElement xml element
     * @param aContext according to context
     * @throws tr.gov.tubitak.uekae.esya.api.xmlsignature.XMLSignatureException
     *          when structure is invalid or can not be
     *          resolved appropriately
     */
    public UnsignedDataObjectProperty(Element aElement, Context aContext)
            throws XMLSignatureException
    {
        super(aElement, aContext);
    }

    @Override
    public String getNamespace()
    {
        return NS_XADES_1_3_2;
    }

    public String getLocalName()
    {
        return TAGX_UNSIGNEDDATAOBJECTPROPERTY;
    }
}
